//This is a copy-and-paste file for demonstrating how to compose large-components for the site.
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

export default class LargeComponentTemplate extends Component {
  render() {
    return (
        <div className={staticFeatures.largeComponentContainer}>
            <div className={websiteStyle.largeComponentBase}>
                {/*Choose largeComponentLayout1, largeComponentLayout2, or largeComponentLayout3*/}
                <div className={websiteStyle.largeComponentLayout1}>
                    <div>1</div>
                </div>
            </div>
        </div> 
    );
  }
}
