//The info form component will take a wesbite user's information and store it in it's state. 
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";
import Space from './spacing.component';

//Determine website styling module from the config file
const websiteStyle = (function() {
    if (configData.STYLE === "modern"){  
        return modernStyle
    }
})();

export default class InfoForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: undefined,
            sname: undefined,
            email: undefined,
            telephone: undefined
        };
    
        this.handleFNameChange = this.handleChange.bind(this);
        this.handleSNameChange = this.handleSNameChange.bind(this);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleTelephoneChange = this.handleTelephoneChange.bind(this);
        
    }

  render() {
    return (
        <div>
            <div className={staticFeatures.textCentered}>
            Your information
            </div>

            <form className={staticFeatures.infoFormContainer}>
                <div className={staticFeatures.infoFormLabel}>
                    First name
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="fname" placeholder="First name"/>

                <div className={staticFeatures.infoFormLabel}>
                    Last name
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="sname" placeholder="Last name"/>

                <div className={staticFeatures.infoFormLabel}>
                    Email
                </div>

                <input className={staticFeatures.infoFormInput} type="email" name="email" placeholder="Email"/>

                <div className={staticFeatures.infoFormLabel}>
                    Contact number
                </div>

                <input className={staticFeatures.infoFormInput} type="tel" name="phone" pattern="^((\+44)|(0)) ?\d{4} ?\d{6}$"
                 placeholder="Telephone"/>

                <Space size="small"/>

                <input className={websiteStyle.menuButton} type="submit" value="Book now" />
            </form>

        </div>
    );
  }
}
