//This component is a date selecting widget.
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//FontAwesome icons for buttons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faChevronLeft, faChevronRight} from '@fortawesome/free-solid-svg-icons'

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

//This is needed data for the date selector

const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
const daysInEachMonth = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

var currentDate = new Date();

var currentYear = currentDate.getFullYear();
var currentMonth = months[parseInt(String(currentDate.getMonth() + 1).padStart(2, '0') - 1)];
var currentMonthIndex = parseInt(String(currentDate.getMonth() + 1).padStart(2, '0') - 1);
var currentDay = String(currentDate.getDate()).padStart(2, '0');

//Generating JSX for date select grid.
function generateDaySelectJSX(monthIndex, dayButtonFunction) {
  var JSXforDayArray = [];

  //Conditional statements for button active or disabled depending on the days in the selected month, and whether it is past that date in time
  var dayIndex;

  if (monthIndex > currentMonthIndex){
    dayIndex = 0;
  }else if (monthIndex === currentMonthIndex){
    dayIndex = currentDay;
  }else if (monthIndex < currentMonthIndex){
    dayIndex = 32;
  }

  for (let day = 1; day < 32; day++){
    if (daysInEachMonth[monthIndex] === 28){
      if (day >= dayIndex && day < 29){
        JSXforDayArray.push(<button onClick={() => dayButtonFunction(day)} className={websiteStyle.dayButton} type="button">{day}</button>);
      }
      else{
        JSXforDayArray.push(<button disabled className={websiteStyle.dayButton} type="button">{day}</button>);
      }
    }else if (daysInEachMonth[monthIndex] === 30){
      if (day >= dayIndex && day < 31){
        JSXforDayArray.push(<button onClick={() => dayButtonFunction(day)} className={websiteStyle.dayButton} type="button">{day}</button>);
      }
      else{
        JSXforDayArray.push(<button disabled className={websiteStyle.dayButton} type="button">{day}</button>);
      }
    }else if (daysInEachMonth[monthIndex] === 31){
      if (day >= dayIndex && day < 32){
        JSXforDayArray.push(<button onClick={() => dayButtonFunction(day)} className={websiteStyle.dayButton} type="button">{day}</button>);
      }
      else{
        JSXforDayArray.push(<button disabled className={websiteStyle.dayButton} type="button">{day}</button>);
      }
    }
  }

  return JSXforDayArray;
};

export default class DateSelector extends Component {
  constructor(props) {
    super(props);

    this.state = {month: currentMonth,
                  day: currentDate,
                  year: currentYear,
                  dayGridJSX: generateDaySelectJSX(months.indexOf(currentMonth) - 1, this.handleDayButton)
                 };
    
    this.handleLeftArrow = this.handleLeftArrow.bind(this);
    this.handleRightArrow = this.handleRightArrow.bind(this);
    this.handleDayButton = this.handleDayButton.bind(this);
  }

  handleLeftArrow(){
    this.setState({
      month: months[months.indexOf(this.state.month) - 1],
      dayGridJSX: generateDaySelectJSX(months.indexOf(this.state.month) - 1, this.handleDayButton)
    });
  }

  handleRightArrow(){
    this.setState({
      month: months[months.indexOf(this.state.month) + 1],
      dayGridJSX: generateDaySelectJSX(months.indexOf(this.state.month) + 1, this.handleDayButton)
    });
  }

  handleDayButton(dayOnButton){
    this.setState({
      day: dayOnButton
    });
  }

  render() {
    return (
        <div className={staticFeatures.dateSelectContainer}>
            <div className={staticFeatures.dayGrid}>
                {this.state.dayGridJSX}
            </div> 

            <div className={staticFeatures.monthSelectorContainer}>
                <button onClick={this.handleLeftArrow} className={websiteStyle.monthArrowButton} type='button'> <FontAwesomeIcon icon={faChevronLeft} /> </button> 

                <div className={websiteStyle.monthDisplay}>
                  <div className={websiteStyle.centerMonthText}>
                    {this.state.month}
                  </div>
                </div>

                <button onClick={this.handleRightArrow} className={websiteStyle.monthArrowButton} type='button'> <FontAwesomeIcon icon={faChevronRight} /> </button> 
            </div>

            <div>{this.state.day}</div>

        </div>
    );
  }
}
