//This component is a time selecting widget. You can retrieve the currently selected date using refs to it's state; state.minute & state.hour.

import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();
//This is needed data for the time selector
//---- Read opening hours from config file
const openingHours = (function() {
    var openingTimes = []

    Object.keys(configData.OPENINGTIMES).forEach(day => {
        var openClose = {
            open: configData.OPENINGTIMES[day].slice(0, 4),
            close: configData.OPENINGTIMES[day].slice(5)
        }

        openingTimes.push(openClose);
    })

    return openingTimes;
})();

const hours = [...Array(25).keys()]

var currentTime = new Date();

export default class TimeSelector extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <div>
            {openingHours}
        </div>
    );
  }
}
