import { BrowserRouter as Router} from "react-router-dom";
import "../src/App.css"

//Loading all styling modules
import gridLayout from "./css-modules/grid-layout.module.css";
import modernStyle from "./css-modules/modern.module.css";

//Loading configuration
import configData from './config/barber-site.config.json';

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

function App() {
  return (
    <div>
      {/*Main grid container*/}
      <div className={gridLayout.mainGrid}>

        <div className={gridLayout.headerArea}>
          {/*Header grid container*/}
          <div className={gridLayout.headerGrid}>
            <div>logo</div>
            <div className={websiteStyle.h1}>{configData.TITLE}</div>
            <div>badge</div>
          </div>

        </div>

        {/*Menu*/}
        <div className={gridLayout.menuArea}>
          <button className={websiteStyle.menuButton} type="button">Sub-page Name</button>
          <button className={websiteStyle.menuButton} type="button">Sub-page Name</button>
          <button className={websiteStyle.menuButton} type="button">Sub-page Name</button>
        </div>

        {/*Main content*/}
        <div className={gridLayout.mainArea}>
          Main
        </div>

        {/*Footer content*/}
        <div className={gridLayout.footerArea}>
          Footer
        </div>
      </div>

      <Router>

      </Router>

    </div>
  );
}

export default App;
