//This is a awards panel component which can display awards won by the business. It will size it's self to whatever parent div it is placed for re-usability.
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Loading configuration
import configData from '../config/barber-site.config.json';

//Importing styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Importing award icons
function importAllImages(importFunction) {
  return importFunction.keys().map(importFunction);
}

const galleryImages = importAllImages(require.context('../media/awards-icons/', false, /\.(png|jpe?g|svg)$/));

//Determine website styling module from the config file
const websiteStyle = (function() {
    if (configData.STYLE === "modern"){
      return modernStyle
    }
  })();

export default class Awards extends Component {
  render() {
    return (
      <div className={[staticFeatures.awardsContainer, websiteStyle.awardsContainer].join(' ')}>
        Awards
      </div>
    );
  }
}
