//This component is a date selecting widget.
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//FontAwesome icons for buttons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faChevronLeft, faChevronRight} from '@fortawesome/free-solid-svg-icons'

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

//This is needed data for the date selector

const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
const daysInEachMonth = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

var currentDate = new Date();

var currentYear = currentDate.getFullYear();
var currentMonth = months[parseInt(String(currentDate.getMonth() + 1).padStart(2, '0') - 1)];
var currentMonthIndex = String(currentDate.getMonth() + 1).padStart(2, '0')
var currentDay = String(currentDate.getDate()).padStart(2, '0');

//Generating JSX for date select grid.
const daySelectJSX = (function() {
  var JSXforDayArray = [];

  //Conditioal statments for button active or disabled depending on the days in the current month
  for (let day = 1; day < 32; day++){
    if (daysInEachMonth[currentMonthIndex] == 28){
      if (day < 29){
        JSXforDayArray.push(<button className={websiteStyle.dayButton} type="button">{day}</button>);
      }
      else{
        JSXforDayArray.push(<button disabled className={websiteStyle.dayButton} type="button">{day}</button>);
      }
    }else if (daysInEachMonth[currentMonthIndex] == 30){
      if (day < 31){
        JSXforDayArray.push(<button className={websiteStyle.dayButton} type="button">{day}</button>);
      }
      else{
        JSXforDayArray.push(<button disabled className={websiteStyle.dayButton} type="button">{day}</button>);
      }
    }else{
        JSXforDayArray.push(<button className={websiteStyle.dayButton} type="button">{day}</button>);
    }
  }

  return JSXforDayArray;
})();

export default class DateSelector extends Component {
  constructor(props) {
    super(props);

    this.state = {month: undefined,
                  day: undefined,
                  year: undefined
                 };
  }

  render() {
    return (
        <div className={staticFeatures.dateSelectContainer}>
            <div className={staticFeatures.dayGrid}>
                {daySelectJSX}
            </div> 

            <div className={staticFeatures.monthSelectorContainer}>
                <button className={websiteStyle.monthArrowButton} type='button'> <FontAwesomeIcon icon={faChevronLeft} /> </button> 

                <div className={websiteStyle.monthDisplay}>
                  <div className={websiteStyle.centerMonthText}>
                    {month}
                  </div>
                </div>

                <button className={websiteStyle.monthArrowButton} type='button'> <FontAwesomeIcon icon={faChevronRight} /> </button> 
            </div>

        </div>
    );
  }
}
