//This is a booking component which is set-up based on the provided config file.
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Importing sub-components
import DateSelector from './date-select.component';

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

export default class Booking extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedDate: undefined
    };

    this.dateSelectorRef = React.createRef();

    this.getDateSelected = this.getDateSelected.bind(this);
  }

  getDateSelected(){
    const currentDateSelector = this.dateSelectorRef.current

    const daySelected = currentDateSelector.state.day;
    const monthSelected = currentDateSelector.state.month;


    this.setState({
      selectedDate: {daySelected, monthSelected}
    });
  }

  render() {
    return (
        <div className={staticFeatures.bookingComponentContainer}>
            <div className={websiteStyle.booking}>
              <DateSelector ref={this.dateSelectorRef}/>

              <button onClick={this.getDateSelected} type='button'>Select date.</button>

              {this.state.selectedDate}
            </div>
        </div> 
    );
  }
}
