//The info form component will take a wesbite user's information and store it in it's state. 
import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";
import Space from './spacing.component';

//Determine website styling module from the config file
const websiteStyle = (function() {
    if (configData.STYLE === "modern"){  
        return modernStyle
    }
})();

export default class InfoForm extends Component {
  render() {
    return (
        <div>
            <div className={staticFeatures.textCentered}>
            Your information
            </div>

            <form className={staticFeatures.infoFormContainer}>
                <div className={staticFeatures.infoFormLabel}>
                    First name
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="fname"/>

                <div className={staticFeatures.infoFormLabel}>
                    Last name
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="sname"/>

                <div className={staticFeatures.infoFormLabel}>
                    Email
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="email"/>

                <div className={staticFeatures.infoFormLabel}>
                    Contact number
                </div>

                <input className={staticFeatures.infoFormInput} type="text" name="phone"/>

                <Space size="small"/>

                <input className={websiteStyle.menuButton} type="submit" value="Submit" />
            </form>

        </div>
    );
  }
}
