//Webpage is a component which can be conceptualised as the translator from a configuration file to a single page application. It will generate a single webpage application from a config file using
//the properties given there.

import React, { Component } from 'react';

import styles from "./webpage.module.css";

import 'bootstrap/dist/css/bootstrap.css';

import $ from 'jquery'; 

//import {getColour} from "../config/config-lib";

export default class Webpage extends Component {
  constructor(props){
    super(props);

    this.state = {
      subpage1: "hi"
    };

    this.renderSubpage = this.renderSubpage.bind(this);
  }

  renderSubpage(){
    $("sub-page-placeholder").html("subpage");
  }

  render() {
    
    const configData = this.props.configData;

    return (
      <div className={styles.mainGrid}>
        <div className={styles.headerArea}>
          <div className={styles.headerGrid}>
            <div className={styles.headerItem}>logo</div>
            <div className={styles.headerItem}>title</div>
            <div className={styles.headerItem}>badge</div>
          </div>
        </div>

        <div className={styles.menuArea}>
          <button onClick={this.renderSubpage} className={styles.menuButton} type="button">Sub-page Name</button>
        </div>

        <div className={styles.mainArea}>
          <div id="sub-page-placeholder"></div>
        </div> 

        <div className={styles.rightArea}>
          Right
        </div>

        <div className={styles.footerArea}>
          Footer
        </div>
      </div>
    );
  }
}
