import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import { Link } from 'react-router-dom';
import "../src/App.css"

//Loading all styling modules
import staticFeatures from "./css-modules/static.module.css";
import modernStyle from "./css-modules/modern.module.css";

//Loading configuration
import configData from './config/barber-site.config.json';

//Loading components
import HomePage from "./components/home.page";

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

//Used to refresh page
function refresh(){
  setTimeout(function(){
    window.location.reload();
  });
}

function App() {
  return (
    <div>  
      {/*Header: it is setup as a grid layout with 3 positions; left, middle and right.*/}
      <div className={websiteStyle.headerStyling}>
        <div className={staticFeatures.headerContainer}>
              <div className={staticFeatures.title}>
               <div className={websiteStyle.h1}>{configData.TITLE}</div>
             </div>
        </div>
      </div>

      {/*Below the header, the rest of the screen will be composed to a menu bar to the left and to the page of the site the user is on. Done using a 1x2 "grid".*/}
      <div className={staticFeatures.restOfScreenContainer}>
        {/*Menu: it can be conceptualised as a stack of buttons which re-route the user to the desired page.*/}
        <div className={websiteStyle.menuStyling}>
          <Router>
            <div className={staticFeatures.menuButton}>
              <Link to="/home">
                <button onClick={refresh} className={websiteStyle.menuButton} type="button">Home</button>
              </Link>
            </div>

            <div className={staticFeatures.menuButton}>
              <button className={websiteStyle.menuButton} type="button">About us</button>
            </div>

            <div className={staticFeatures.menuButton}>
              <button className={websiteStyle.menuButton} type="button">Bookings</button>
            </div>
          </Router>
        </div>

        {/*Page: this will dynamically render pages of the websites depending on the url the user is currently on using react-router-dom.*/}
        <div>
          <Router>
            <Routes>
              <Route path="/" element={<HomePage/>}/>
              <Route path="/home" element={<HomePage/>}/>
            </Routes>
          </Router>
        </div>

      </div>
    </div>
  );
}

export default App;
