import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

//Importing bootstrap components
import Carousel from 'react-bootstrap/Carousel'

//Loading configuration
import configData from '../config/barber-site.config.json';

//Loading all styling modules
import staticFeatures from "../css-modules/static.module.css";
import modernStyle from "../css-modules/modern.module.css";

//Importing gallery images
function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(require.context('../media/gallery-images/', false, /\.(png|jpe?g|svg)$/));

console.log(images);

//Determine website styling module from the config file
const websiteStyle = (function() {
  if (configData.STYLE === "modern"){
    return modernStyle
  }
})();

export default class Gallery extends Component {
  render() {
    return (
    <div className={staticFeatures.galleryContainer}>
        <Carousel className={staticFeatures.carousel}>
            <Carousel.Item className={staticFeatures.galleryImage}>
                <img className={staticFeatures.galleryImage} src={images[0]} alt="First slide"/>
                
                <Carousel.Caption>
                <h3>First slide label</h3>
                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item className={staticFeatures.galleryImage}>
                <img className={staticFeatures.galleryImage} alt="First slide"/>
                
                <Carousel.Caption>
                <h3>First slide label</h3>
                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    </div>
    );
  }
}
